    options.headers={
      'Content-Type':'application/json'
    }
    return fetch('/loginOk',options,{credentials:'include'})
      .then(checkStatus)
      .then(parseJSON)
      .then((res)=>{
        if(res.retCode === '0001'){
          localStorage.setItem('x-access-token',res.retBody.AccessToken)
          return 'success'
        }
        else if(res.retCode === '0002'){
          return 'error'
        }
        else if(res.retCode === '0003'){
          return 'error'
        }else{
          return ;
        }

      })
      .catch(err=>({err}))
  }

