      // 'Access-Control-Allow-Origin': '*'
    }

  }


  delete options.data
  delete options.param

  return fetch(commonUrl + url,options,{credentials: 'include'})
    .then(checkStatus)
    .then(parseJSON)
    .catch(err=>({err}))
}

